package com.example.dodo.geologistica;import android.content.ClipData;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ArrayAdapter;import android.widget.TextView;import java.util.ArrayList;/** * Created by dodo on 22/08/16. */public class ListaAdapterOs extends ArrayAdapter<Os> {    private Context context;    private ArrayList<Os> list;    public ListaAdapterOs(Context context, ArrayList<Os> list){        super(context, 0, list);        this.context = context;        this.list = list;    }    @Override    public View getView(int position, View convertView, ViewGroup parent) {        Os osItem = this.list.get(position);        convertView = LayoutInflater.from(this.context).inflate(R.layout.os, null);        TextView textViewIdOs = (TextView) convertView.findViewById(R.id.idOs);        textViewIdOs.setText(String.valueOf(osItem.getIdOs()));        TextView textViewData = (TextView) convertView.findViewById(R.id.data);        textViewData.setText(osItem.getData());        TextView textViewHora = (TextView) convertView.findViewById(R.id.hora);        textViewHora.setText(osItem.getHora());        TextView textViewEndereco = (TextView) convertView.findViewById(R.id.endereco);        textViewEndereco.setText(osItem.getEndereco());        return  convertView;    }}